current_index = 0

@func.route('/func')
def new_diet(x):
    global current_index
    item = x[current_index]
    current_index = (current_index + 1) % len(x)
    return item

@diet.route('/diet')
def return_diet():
    next_item = new_diet(food_diet)
    diet_items = next_item.split('\n')  # Split the items into a list using '\n'
    return render_template('diet.html', diet_items=diet_items)
